import React from "react";
import haze from "./images/image-jeremy.png"
class Card1 extends React.Component{
    constructor(props){
        super(props)
        this.state = {temps : "daily"}
        this.change = this.change.bind(This)
    }
    change = (x)=>{
        this.props.changes(x)
    }
    render(){
        return(
            <div className="jeremy bg-secondary">
                <div className="bg-danger card1  ">
                    <img className="ms-5 mb-3  " width="80" src={haze} alt="" />
                    <p className="ms-5 m-0">report for</p>
                    <h1 className="ms-5">Jeremy <br />Robson</h1>
                </div>
                    <div className="" >
                    <p className="ms-5 mt-4" onClick={()=>this.change("daily")}>Daily</p>
                    <p className="ms-5" onClick={()=>this.change('weekly')} >Weekly</p>
                    <p className="ms-5 " onClick={()=>{this.change("monthly")}} >Monthly</p>
                    </div>
            </div>
        )
    }
}
export default Card1
